import React, {Component} from 'react';
import CardList from './CardList';
import Card from './Card';
import SearchBox from './SearchBox';
import { users } from './users';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from "react-router-dom";

export class App extends Component {

  constructor() {
    super()
    this.state = {
      users: [],
      search_field: ''
    }
  }

  Upload = () => {
  return <h2> Upload </h2>;
}

  MainDisplay() {
  return <div className = "tc">
          <div className = "main-display">
            <h1 className = "f-subheadline lh-title"> Men's Shed App </h1>                                                                                          
            {/*<SearchBox searchChange = {this.onSearchChange} /> */}
            <a className="f6 grow no-underline br-pill ph3 pv2 mb2 dib white bg-black" href =  "#" >Add Story</a>
            <Link to = "/upload"> Upload a Story </Link>
            </div>
  </div> 
}

  // componentDidMount() {
    //might need this later
  // }

  onSearchChange = (event) => {
    this.setState({search_field: event.target.value})
  }


   handleClick = () => {
    this.setState({
      users: [this.state.users, 
              <Card
              key = {0} 
              id = {users[0].id} 
              name  = {users[0].name}
              date = {users[0].date}
              title = {users[0].title}
              description = {users[0].description}
              />]
    })
  }

  render() {

    // const filteredUsers = this.state.users.filter( users => {
    //   return this.users.name.toLowerCase().includes(this.state.search_field.toLowerCase());
    // }) 

    return (
      <Router>
        <ul>
          <li> 
          <Link to = "/disp"> View Stories </Link>
          </li>
          <li>
          <Link to = "/upload"> Upload </Link>
          </li>
        </ul>

        <Switch>

          <Route path="/disp">
            <MainDisplay />  
          </Route>

          <Route path="/upload">
            <Upload />  
          </Route>

        </Switch>
        {this.state.users} 
          {/* <CardList users = {filteredUsers} /> */}
      </Router>
    );
  }

}





export default App;
